if (typeof define !== 'function') { var define = require('amdefine')(module) }
define( function(require,exports,module)
{
    const MenuView = require('./menu_view').View;

    class RowMenuView extends MenuView {
        constructor(params) {
            super(params);
            this.addAction("add_row",          this.addRow.bind(this));
            this.addAction("add_row_multiple", this.addRowMultiple.bind(this));
            this.addAction("remove_row",       this.removeRow.bind(this));
        }

        addRow(){
            var row_position = this.es_controller.table.getSheet().indexForRow(this.current_element) + 1;
            if(row_position == -1) return;
            this.es_controller.table.getSheet().insertRow(row_position);
        }

        addRowMultiple(){
            var row_position = this.es_controller.table.getSheet().indexForRow(this.current_element) + 1;
            if(row_position == -1) return;
            for(var i = 0; i <= 11;i++){
                this.es_controller.table.getSheet().insertRow(i + row_position);
            }
        }

        removeRow(){
            this.es_controller.table.getSheet().deleteRow(this.current_element);
        }
    }

    module.exports.View = RowMenuView;
});